contract BluePlan {

    // 每秒产出利息
    uint public interest;
    address public rewardToken;
    address public owner;
    
    struct User{
        uint userReward;
        bool userstate;
    }


    mapping(address => User) public User_Map;






    constructor (uint _interest, address _rewardToken) {
        interest = _interest;
        rewardToken = _rewardToken;
        owner = msg.sender;
    }






}

